'use strict';

angular.module('weCareDeliveryApp',['ngCookies',
                                    'ngResource',
                                    'ngRoute',
                                    'ngSanitize',
                                    'utf8-base64',
                                    'ui.bootstrap'])
.config(function($routeProvider) {
    $routeProvider

        .when('/', {
            templateUrl : 'views/login.html',
            controller  : 'loginController'
        })

        .when('/login', {
            templateUrl : 'views/login.html',
            controller  : 'loginController'
        })

        .when('/deliveryType', {
            templateUrl : 'views/deliveryType.html',
            controller  : 'deliveryTypeController'
        })
        
        .when('/transferOrdersToDriver', {
        	templateUrl : 'views/transferOrdersToDriver.html',
        	controller  : 'transferOrdersToDriverController'
        })
        
        .when('/deliverOrders', {
        	templateUrl : 'views/deliverOrders.html',
        	controller  : 'deliverOrdersController'
        })
        .when('/verifyDeliverOrders', {
        	templateUrl : 'views/verifyDeliverOrders.html',
        	controller  : 'verifyDeliverOrdersController'
        })
        .when('/scanIn', {
        	templateUrl : 'views/storeScanInReturns.html',
        	controller  : 'storeScanInReturnsController'
        })        
        .when('/error', {
        	templateUrl : 'error.html',
        	controller  : 'verifyDeliverOrdersController'
        })
        .otherwise({
            redirectTo  : '/error'
        })

})
.run(function () {
    window.bridgeInitialised = false;
});
